import os
from Crypto.Util.number import getPrime, bytes_to_long


def main():
    p, q, r = getPrime(1024), getPrime(1024), getPrime(1010)
    n = p * q
    secret = (p + q) % r

    e = 31337
    pt = bytes_to_long((os.getenv("FLAG").encode()))
    ct = pow(pt, e, n)

    print(f"n = {n}")
    print(f"e = {e}")
    print(f"ct = {ct}")
    print(f"r = {r}")
    print(f"secret = {secret}")

    # n = 
    # e = 31337
    # ct = 
    # r = 
    # secret = 

if __name__ == "__main__":
    main()
